export default {
  name: 'CircleAnimation',
  props: {
    msg: String
  },
  data() {
      return {
          circle:{
            circle_width: 300,
            circle_height: 300,
            circle_line: 10,//圆环
            color: '#4169e1'
      } 
      }   
  },
  mounted: function(){
      console.log(this.circle.circle_width)
      console.log(this.$refs.canvas)
      let ctx = this.$refs.canvas.getContext('2d');
    //   ctx.fillRect (10, 10, 55, 50);
      let circleObj = {
          ctx: ctx,
          /*圆心*/
          x: this.circle.circle_width/2,
          y: this.circle.circle_height/2,
          /*半径*/
          radius: this.circle.circle_width/2 - this.circle_line
      };

        /*灰色的圆环*/
        circleObj.startAngle = Math.PI * 2;
        circleObj.endAngle = Math.PI * 2 / 4; // 对应 360° 的 1/4 即 90°
        circleObj.color = '#e9e9e9';
        drawCircle(circleObj);

        /*有色的圆环*/
        /*开始的度数-从上一个结束的位置开始*/
        circleObj.startAngle = circleObj.endAngle;
        /*结束的度数 Math.PI * 2 */
        circleObj.endAngle = Math.PI * 2;
        circleObj.color = "#dd6200";
        drawCircle(circleObj);

        ctx.lineWidth = 7;

ctx.beginPath(); // 开一条新路
ctx.moveTo(170, 120);

var grd=ctx.createLinearGradient(0,0,170,0);
grd.addColorStop("0","black");
grd.addColorStop("0.3","magenta");
grd.addColorStop("0.5","blue");
grd.addColorStop("0.6","green");
grd.addColorStop("0.8","yellow");
grd.addColorStop(1,"red");

ctx.strokeStyle = grd;
ctx.arc(120, 120, 50, 0 ,Math.PI*2);
ctx.stroke(); // 画圆

      drawCircle(circleObj);

      function drawCircle (circleObj){
        let ctx = circleObj.ctx;
        ctx.beginPath();
        ctx.arc(circleObj.x, circleObj.y, circleObj.radius, circleObj.startAngle, circleObj.endAngle, false);
        //设定曲线粗细度
        ctx.lineWidth = circleObj.lineWidth;
        //给曲线着色
        ctx.strokeStyle = circleObj.color;
        //连接处样式
        ctx.lineCap = 'round';
        //给环着色
        ctx.stroke();
        ctx.closePath();
      }
  }
}
